// @ts-ignore
import React, {ReactElement} from 'react';

import {useAuth} from '../contexts/AuthContext';
import {Breadcrumb, Layout, Menu, MenuProps, theme} from "antd";
import Sider from "antd/lib/layout/Sider";
import {Content, Footer, Header} from "antd/lib/layout/layout";
import {
  AppstoreOutlined,
  BarChartOutlined,
  CloudOutlined, LaptopOutlined, NotificationOutlined, ShopOutlined, TeamOutlined,
  UploadOutlined,
  UserOutlined,
  VideoCameraOutlined
} from "@ant-design/icons";
import {useNavigate} from 'react-router-dom';

export function PageLayout(): ReactElement {
  const {nickname, logout, isLogin} = useAuth();

  const {Header, Content, Sider} = Layout;

  const rightMenusForLogin: MenuProps['items'] = [
    {
      key: 'profile',
      label: nickname,
    },
    {
      key: 'logout',
      label: `로그아웃`,
    }
  ]

  const rightMenusForNotLogin: MenuProps['items'] = [
    {
      key: 'login',
      label: `로그인`,
    },
    {
      key: 'signup',
      label: `회원가입`,
    }
  ]

  const items2: MenuProps['items'] = [UserOutlined, LaptopOutlined, NotificationOutlined].map(
    (icon, index) => {
      const key = String(index + 1);

      return {
        key: `sub${key}`,
        icon: React.createElement(icon),
        label: `subnav ${key}`,

        children: new Array(4).fill(null).map((_, j) => {
          const subKey = index * 4 + j + 1;
          return {
            key: subKey,
            label: `option${subKey}`,
          };
        }),
      };
    },
  );

  const {
    token: {colorBgContainer},
  } = theme.useToken();

  const navigate = useNavigate();

  const handleGnbMenuClick = (e: any) => {
    switch (e.key) {
      case 'login':
        navigate('/login');
        break;
      case 'signup':
        navigate('/signup');
        break;
      case 'logout':
        break;
      case 'profile':
        navigate(`/members/${nickname}`)
        break;
      default:
        navigate('/');
    }
  };

  return (
    <Layout>
      <Header style={{display: 'flex', alignItems: 'center'}}>
        {/*<div className="demo-logo"/>*/}
        <Menu theme="dark" mode="horizontal" defaultSelectedKeys={['2']} items={[]}/>
        <Menu
          theme="dark"
          mode="horizontal"
          style={{marginLeft: 'auto'}}
          items={isLogin ? rightMenusForLogin : rightMenusForNotLogin}
          onClick={handleGnbMenuClick}
        />
      </Header>
      {isLogin && (
        <Layout>
          <Sider width={200} style={{background: colorBgContainer}}>
            <Menu
              mode="inline"
              defaultSelectedKeys={['1']}
              defaultOpenKeys={['sub1']}
              style={{height: '100%', borderRight: 0}}
              items={items2}
            />
          </Sider>
          <Layout style={{padding: '0 24px 24px'}}>
            <Breadcrumb style={{margin: '16px 0'}}>
              <Breadcrumb.Item>Home</Breadcrumb.Item>
              <Breadcrumb.Item>List</Breadcrumb.Item>
              <Breadcrumb.Item>App</Breadcrumb.Item>
            </Breadcrumb>
            <Content
              style={{
                padding: 24,
                margin: 0,
                minHeight: 280,
                background: colorBgContainer,
              }}
            >
              Content
            </Content>
          </Layout>
        </Layout>
      )}
    </Layout>
  );
}
