import React, { ReactElement, useEffect, useMemo, useState } from 'react';
import { Route, Routes } from 'react-router-dom';
import styled from '@emotion/styled';

import { useAuth } from 'contexts/AuthContext';
import { Main } from 'pages/main';
import { LoadingSpinner } from 'utils/loading';
import {NotFoundErrorPage} from "pages/NotFoundErrorPage";

export function PageLayout(): ReactElement {
  const [isFolded, setIsFolded] = useState<boolean>(false);
  const { nickname, logout, isLogin } = useAuth();

  return (
    <Layout>
      <Content isHidden={false} isFolded={isFolded} style={{ minHeight: '100vh' }}>
        <Routes>
          <Route path="/" element={<Main />} />

          <Route path="/*" element={<NotFoundErrorPage />} />
        </Routes>
      </Content>
    </Layout>
  );
}

const Layout = styled.div`
  background-color: #f0f0f0;`;

const Content = styled.div<{ isFolded: boolean, isHidden: boolean }>`
  transition: all .5s;
  padding: ${({ isHidden, isFolded }) =>
    isHidden ? '20px 20px 80px 20px;' : `80px 20px 80px ${isFolded ? 80 : 270}px;`}

  > article {
    min-width: 767px;
  }
`;
